contracts/array.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

contract Array{
    uint256 myfavoritenumber;

    struct person{
        uint256 favoritenumber;
        string name;
    }
    
    /*uint256[] listOfpeople : this is an dynamic array and if we want to convert it into 
    static we need to put some number into those square bracket*/

      
    // person public pat = person(7,"pat");
    // person public mathew = person(9,"mathew");
    // person public jon = person(11,"jon");

    person[] public listofpeople ; //[]

    function addperson(string memory _name, uint256 _favoritenumber) public {
        listofpeople.push(person(_favoritenumber,_name));
    }
}

contracts/exercise_on_array.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

/*Create a smart contract that can store and view a list of animals. Add manually
three (3) animals and give the possibility to the user to manually add an 
indefinite number of animals into the smart contract.*/


contract Animal{
    // string[3] public listofmyanimals ;

    string[] public listofanimals =  ["cat","cow","zebra"];
    
    function addAnimal(string memory _Animal) public {
        listofanimals.push(_Animal);
        
     }

    function retrieve () public view returns(string[] memory) {
        return listofanimals;
    }
}

contracts/mapping.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

contract Mapping{
    uint256 myfavoritenumber;

    struct person{
        uint256 favoritenumber;
        string name;
    }

    mapping(uint256 => string) public favoritenumbertoname;
    


    person[] public listofpeople ; //[]

    function addperson(string memory _name, uint256 _favoritenumber) public {
        listofpeople.push(person(_favoritenumber,_name));
        favoritenumbertoname[_favoritenumber] = _name;
    
    }

    function getarraylength() public view returns(uint256){
        return listofpeople.length;
    }
}

